import React, {useEffect, useRef} from 'react';
import * as d3 from 'd3';
function Btradel1() {
    const Ref=useRef();
    const data=[{"Year": 1996, "TE": 0.61472, "TI": 0.7568, "TD": -0.14208},
                {"Year": 1997, "TE": 0.71787, "TI": 1.11193, "TD": -0.39406},
                {"Year": 1998, "TE": 0.42711, "TI": 1.09658, "TD": -0.66947},
                {"Year": 1999, "TE": 0.53897, "TI": 1.28269, "TD": -0.74372},
                {"Year": 2000, "TE": 0.83123, "TI": 1.50199, "TD": -0.67076},
                {"Year": 2001, "TE": 0.95184, "TI": 2.036215, "TD": -1.08431}
                 ];
    useEffect(()=>{
        const height="400px";
        const width="900px";
        const svg=d3.select(Ref.current);
                svg.attr("width",width)
                    .attr("height",height)
                    .style("background-color","pink");
        var out=["red","orange","yellow","green"]
        var myScale=d3.scaleLinear()
            .domain([0,d3.max(data)])
            .range([0,500]);
        var coScale=d3.scaleOrdinal()
            .domain(data)
            .range(out);
        var yScale=d3.scaleLinear()
                .domain([-3,3])
                .range([350,0]);
        var xScale=d3.scaleLinear()
                .domain([1996,2001])
                .range([0,600]);
        var yAxis=d3.axisLeft().scale(yScale)
            .tickFormat(function(d){return d+"%";});
        var xAxis=d3.axisBottom().scale(xScale);
        svg.append("g").attr("transform","translate(75,25)").call(yAxis);
        svg.append("g").attr("transform","translate(75,200)").call(xAxis);
        var bar=svg.selectAll("rect").data(data);
        bar.enter().append("rect")
            .attr("width",function(d){return 25+"px";})
            .attr("x",function(d,i){return 70+xScale(i);})
            .attr("y",0)
            .transition().duration(300)
            .attr("height",function(d){return myScale(d)+"px";})
            .attr("y",function(d){return (570-myScale(d))+"px";})
            .style("fill",function(d){return coScale(d);});
        var data1=svg.selectAll("text").data(data);
        data1.enter().append("text")
            .text(function(d){return d;})
            .attr("x",function(d,i){return 70+xScale(i);})
            .attr("y",function(d){return (570-myScale(d))-2+"px";})
            .style("color","crimson");
        }
    );
    return (
        <div>
            <svg ref={Ref}></svg>
        </div>
    )
}

export default Btradel1;
